&lt;pre&gt;&lt;code class=&#34;java&#34;&gt;int num=34, max=30;
if ( num &amp;gt;= max*2 )
    System.out.println(&amp;quot;zhang&amp;quot;);
    System.out.println(&amp;quot;huang&amp;quot;);
System.out.println(&amp;quot;zhu&amp;quot;);
&lt;/code&gt;&lt;/pre&gt;
&lt;blockquote&gt;
&lt;p&gt;huang&lt;br&gt;zhu&lt;/p&gt;
&lt;/blockquote&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;写出以下代码段的执行结果： {.quiz .essay}&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;java&#34;&gt;int limit = 100;
int num1 = 15;
int num2 = 40;
if ( limit &amp;lt;= limit)
&amp;#123;
    if ( num1 == num2 )
        System.out.println(&amp;quot;lemon&amp;quot;);
    System.out.println(&amp;quot;lime&amp;quot;);
&amp;#125;
System.out.println(&amp;quot;grape&amp;quot;);
&lt;/code&gt;&lt;/pre&gt;
&lt;blockquote&gt;
&lt;p&gt;lime&lt;br&gt;grape&lt;/p&gt;
&lt;/blockquote&gt;
