&lt;pre&gt;&lt;code class=&#34;sql&#34;&gt;CREATE TABLE &amp;quot;topic&amp;quot; (
    &amp;quot;id&amp;quot; serial NOT NULL PRIMARY KEY,
    &amp;quot;forum_id&amp;quot; integer NOT NULL,
    &amp;quot;subject&amp;quot; varchar(255) NOT NULL
);
ALTER TABLE &amp;quot;topic&amp;quot;
ADD CONSTRAINT forum_id FOREIGN KEY (&amp;quot;forum_id&amp;quot;)
