&lt;pre&gt;&lt;code class=&#34;ruby&#34;&gt;# The Greeter class
class Greeter
  def initialize(name)
    @name = name.capitalize
  end

  def salute
    puts &amp;quot;Hello #&amp;#123;@name&amp;#125;!&amp;quot;
  end
end

g = Greeter.new(&amp;quot;world&amp;quot;)
g.salute
&lt;/code&gt;&lt;/pre&gt;
&lt;pre&gt;&lt;code class=&#34;makefile&#34;&gt;# Makefile

BUILDDIR      = _build
EXTRAS       ?= $(BUILDDIR)/extras

.PHONY: main clean

main:
    @echo &amp;quot;Building main facility...&amp;quot;
    build_main $(BUILDDIR)

clean:
    rm -rf $(BUILDDIR)/*
